(38, '<h2>\r\n	Parametry jakościowe w skupie</h2>\r\n<div>\r\n	&nbsp;</div>\r\n<ul>\r\n	<li>\r\n		<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">wilgotność do 14,5%,</span></span><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 16px;"><strong style="color: rgb(69, 59, 32); font-family: Verdana, Geneva, sans-serif; line-height: 17px; text-align: justify;"><img alt="" src="http://bzk.wulkanizacja-szczuczyn.pl/admin/js/ckfinder/userfiles/images/images (14).jpg" style="width: 251px; height: 201px; float: right; margin: 10px;" /></strong></span></span></li>\r\n	<li>\r\n		<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">zawartość zanieczyszczeń og&oacute;łem do 10,0%, w tym zanieczyszczenia&nbsp;nieużyteczne do 2,0%,</span></span></li>\r\n	<li>\r\n		<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">zawartość skrobi w suchej masie nie mniej niż 71,0%,</span></span></li>\r\n	<li>\r\n		<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">kukurydza powinna być:</span></span>\r\n		<ul>\r\n			<li>\r\n				<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">o typowej barwie,</span></span></li>\r\n			<li>\r\n				<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">wolna od obcych zapach&oacute;w,</span></span></li>\r\n			<li>\r\n				<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">żywych szkodnik&oacute;w (w tym roztoczy) na każdym etapie rozwoju,</span></span></li>\r\n			<li>\r\n				<span style="font-family:tahoma,geneva,sans-serif;"><span style="font-size: 18px;">wolna od chor&oacute;b grzybowych.</span></span></li>\r\n		</ul>\r\n	</li>\r\n</ul>\r\n', 'center', 'Kukurydza – parametry jakościowe w skupie', 15),
